mapper.select:
select data.rn ,data.BNO ,data.TITLE ,data.CONTENT ,data.REG_DATE ,data.MOD_DATE ,data.VIEW_COUNT ,data.SECRET_TYPE ,data.BOARD_TYPE ,data.CATE_NUM ,data.ID ,data.FILE_CHECK /* Y이면 이미지 보여줌 */ ,data.CATE_KIND ,(select count(*) from reply r where r.bno = data.bno) replyCnt from ( select rownum rn, data.* from ( SELECT B.* , C.CATE_KIND, NVL2(BA.UUID,'Y','N') FILE_CHECK FROM BOARD B INNER JOIN CATEGORY C ON B.CATE_NUM = C.CATE_NUM LEFT OUTER JOIN BOARDATTACH BA ON B.BNO = BA.BNO WHERE B.BNO > 0 AND BOARD_TYPE IN ('01')
AND b.CATE_NUM = #{cate_num}
AND b.TITLE LIKE '%' || #{keyword} || '%'
AND b.CONTENT LIKE '%' || #{keyword} || '%'
order by b.bno desc )data ) data where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

mapper.update:
update board set
	title = #{title},
	content = #{content},
	cate_num = #{cate_num}
where bno = #{bno}
